import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";

const ContractDetail = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);

  useEffect(() => {
    fetch("/contracts.json")
      .then((res) => res.json())
      .then((data) => {
        const found = data.find((c) => c.id === id);
        setContract(found);
      });
  }, [id]);

  if (!contract)
    return <p className="p-6 text-indigo-500">Loading contract details...</p>;

  const riskColor =
    contract.risk === "Low"
      ? "bg-green-200 text-green-800"
      : contract.risk === "Medium"
      ? "bg-yellow-200 text-yellow-800"
      : "bg-red-200 text-red-800";

  return (
    <div className="flex h-screen">
      <Sidebar />
      <div className="flex-1 flex flex-col overflow-auto">
        <Topbar />
        <div className="p-6">
          <h2 className="text-3xl font-bold mb-2 text-indigo-600">
            {contract.name}
          </h2>
          <div className="mb-4 space-y-1">
            <p>
              <strong>Parties:</strong> {contract.parties}
            </p>
            <p>
              <strong>Start Date:</strong> {contract.start}
            </p>
            <p>
              <strong>Expiry Date:</strong> {contract.expiry}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span className="px-2 py-1 rounded bg-blue-200 text-blue-800">
                {contract.status}
              </span>
            </p>
            <p>
              <strong>Risk:</strong>{" "}
              <span className={`px-2 py-1 rounded ${riskColor}`}>
                {contract.risk}
              </span>
            </p>
          </div>

          {/* Clauses */}
          <h3 className="text-xl font-semibold mt-6 mb-2">Clauses</h3>
          <div className="grid md:grid-cols-2 gap-4">
            {contract.clauses.map((c, idx) => (
              <div
                key={idx}
                className="p-4 border rounded-lg shadow-sm hover:shadow-md transition"
              >
                <h4 className="font-bold text-indigo-600">{c.title}</h4>
                <p className="text-gray-700">{c.summary}</p>
                <p className="text-sm text-gray-500">
                  Confidence: {(c.confidence * 100).toFixed(0)}%
                </p>
              </div>
            ))}
          </div>

          {/* Insights */}
          <h3 className="text-xl font-semibold mt-6 mb-2">AI Insights</h3>
          <ul className="space-y-2">
            {contract.insights.map((i, idx) => {
              const insightColor =
                i.risk === "High"
                  ? "bg-red-200 text-red-800"
                  : i.risk === "Medium"
                  ? "bg-yellow-200 text-yellow-800"
                  : "bg-green-200 text-green-800";
              return (
                <li
                  key={idx}
                  className={`p-3 rounded ${insightColor} shadow-sm`}
                >
                  {i.message}
                </li>
              );
            })}
          </ul>

          {/* Evidence */}
          <h3 className="text-xl font-semibold mt-6 mb-2">Evidence</h3>
          <div className="grid md:grid-cols-2 gap-4">
            {contract.evidence.map((e, idx) => (
              <div
                key={idx}
                className="p-3 border rounded-lg shadow-sm hover:shadow-md transition"
              >
                <p>
                  <strong>Source:</strong> {e.source}
                </p>
                <p>{e.snippet}</p>
                <p className="text-sm text-gray-500">
                  Relevance: {(e.relevance * 100).toFixed(0)}%
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContractDetail;
