import React, { useState } from "react";
import { XMarkIcon, CloudArrowUpIcon, CheckCircleIcon, ExclamationCircleIcon } from "@heroicons/react/24/outline";

const UploadModal = ({ isOpen, onClose }) => {
  const [files, setFiles] = useState([]);

  const handleFiles = (selectedFiles) => {
    const newFiles = Array.from(selectedFiles).map((file) => ({
      file,
      status: "Uploading", // Initial status
    }));

    setFiles((prev) => [...prev, ...newFiles]);

    // Simulate upload for each file
    newFiles.forEach((f, index) => {
      setTimeout(() => {
        setFiles((prev) =>
          prev.map((pf) =>
            pf.file.name === f.file.name
              ? { ...pf, status: Math.random() > 0.1 ? "Success" : "Error" }
              : pf
          )
        );
      }, 1500 + index * 500);
    });
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFiles(e.dataTransfer.files);
  };

  const handleBrowse = (e) => {
    handleFiles(e.target.files);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white w-96 rounded-lg shadow-lg p-6 relative">
        <button
          onClick={onClose}
          className="absolute top-3 right-3 text-gray-500 hover:text-gray-700"
        >
          <XMarkIcon className="w-6 h-6" />
        </button>

        <h2 className="text-xl font-bold text-gray-700 mb-4">Upload Files</h2>

        {/* Drag & Drop Area */}
        <div
          onDrop={handleDrop}
          onDragOver={(e) => e.preventDefault()}
          className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center cursor-pointer hover:border-indigo-500 transition"
        >
          <CloudArrowUpIcon className="w-12 h-12 mx-auto text-indigo-500 mb-2" />
          <p className="text-gray-600 mb-2">Drag & drop files here</p>
          <p className="text-gray-500 text-sm">or browse to select files</p>
          <input
            type="file"
            multiple
            onChange={handleBrowse}
            className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          />
        </div>

        {/* Uploaded Files List */}
        <div className="mt-4 max-h-64 overflow-y-auto">
          {files.map((f, idx) => (
            <div
              key={idx}
              className="flex items-center justify-between p-2 border-b last:border-b-0"
            >
              <span className="truncate">{f.file.name}</span>
              <span className="flex items-center gap-1">
                {f.status === "Uploading" && (
                  <span className="text-gray-500 text-sm animate-pulse">Uploading...</span>
                )}
                {f.status === "Success" && (
                  <CheckCircleIcon className="w-5 h-5 text-green-500" />
                )}
                {f.status === "Error" && (
                  <ExclamationCircleIcon className="w-5 h-5 text-red-500" />
                )}
                <span className="text-sm text-gray-600">{f.status}</span>
              </span>
            </div>
          ))}
          {files.length === 0 && (
            <p className="text-gray-400 text-sm mt-2 text-center">
              No files uploaded yet.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UploadModal;
