import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

const ContractDetail = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    fetch("/contracts.json")
      .then((res) => res.json())
      .then((data) => {
        const c = data.find((item) => item.id === id);
        if (c) {
          setContract({
            ...c,
            clauses: [
              { title: "Termination", summary: "90 days notice period.", confidence: 0.82 },
              { title: "Liability Cap", summary: "12 months’ fees limit.", confidence: 0.87 },
            ],
            insights: [
              { risk: "High", message: "Liability cap excludes data breach costs." },
              { risk: "Medium", message: "Renewal auto-renews unless cancelled 60 days before expiry." },
            ],
            evidence: [
              { source: "Section 12.2", snippet: "Total liability limited to 12 months’ fees.", relevance: 0.91 },
            ],
          });
        } else {
          setError(true);
        }
        setLoading(false);
      })
      .catch(() => {
        setError(true);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="text-indigo-500 p-6">Loading contract details...</p>;
  if (error || !contract) return <p className="text-red-500 p-6">Contract not found.</p>;

  return (
    <div className="relative p-6">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-4"
      >
        <h2 className="text-4xl font-bold text-indigo-600 mb-2">{contract.name}</h2>
        <p className="text-gray-700">
          <strong>Parties:</strong> {contract.parties} |{" "}
          <strong>Status:</strong> {contract.status} | <strong>Risk:</strong> {contract.risk}
        </p>
        <p className="text-gray-500">
          <strong>Start:</strong> {contract.start || "N/A"} | <strong>Expiry:</strong> {contract.expiry}
        </p>
      </motion.div>

      {/* Clauses */}
      <div>
        <h3 className="text-2xl font-semibold mb-4 text-indigo-500">Clauses</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {contract.clauses.map((cl, idx) => (
            <motion.div
              key={idx}
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 300 }}
              className="p-5 border rounded-lg shadow hover:shadow-lg bg-white transition"
            >
              <h4 className="font-semibold text-lg mb-2">{cl.title}</h4>
              <p className="text-gray-600 mb-2">{cl.summary}</p>
              <p className="text-sm text-gray-400">
                Confidence: {(cl.confidence * 100).toFixed(0)}%
              </p>
            </motion.div>
          ))}
        </div>
      </div>

      {/* AI Insights */}
      <div className="mt-8">
        <h3 className="text-2xl font-semibold mb-4 text-indigo-500">AI Insights</h3>
        <div className="space-y-3">
          {contract.insights.map((ins, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: idx * 0.1 }}
              className="p-4 border-l-4 rounded shadow-sm flex justify-between items-center bg-gray-50 hover:bg-gray-100 transition"
            >
              <span className="text-gray-700">{ins.message}</span>
              <span
                className={`px-3 py-1 rounded text-white font-semibold ${
                  ins.risk === "High"
                    ? "bg-red-600"
                    : ins.risk === "Medium"
                    ? "bg-yellow-500"
                    : "bg-green-600"
                }`}
              >
                {ins.risk}
              </span>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Evidence Drawer Toggle */}
      <button
        onClick={() => setDrawerOpen(true)}
        className="fixed bottom-6 right-6 bg-indigo-600 text-white px-4 py-2 rounded-full shadow-lg hover:bg-indigo-700 transition z-50"
      >
        View Evidence
      </button>

      {/* Evidence Side Drawer */}
      <AnimatePresence>
        {drawerOpen && (
          <>
            {/* Overlay */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.5 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 bg-black z-40"
              onClick={() => setDrawerOpen(false)}
            />

            {/* Drawer */}
            <motion.div
              initial={{ x: "100%" }}
              animate={{ x: 0 }}
              exit={{ x: "100%" }}
              transition={{ type: "spring", stiffness: 300 }}
              className="fixed top-0 right-0 w-96 h-full bg-white shadow-xl z-50 p-6 overflow-y-auto"
            >
              <h3 className="text-2xl font-semibold mb-4 text-indigo-500 flex justify-between items-center">
                Evidence
                <button
                  onClick={() => setDrawerOpen(false)}
                  className="text-gray-500 hover:text-gray-700 text-xl font-bold"
                >
                  ✕
                </button>
              </h3>
              <div className="space-y-3">
                {contract.evidence.map((ev, idx) => (
                  <motion.div
                    key={idx}
                    whileHover={{ scale: 1.02 }}
                    transition={{ type: "spring", stiffness: 250 }}
                    className="p-4 border rounded-lg bg-gray-50 shadow-sm flex justify-between items-center"
                  >
                    <div>
                      <p className="font-medium text-gray-700">{ev.source}</p>
                      <p className="text-gray-600">{ev.snippet}</p>
                    </div>
                    <span className="text-sm text-gray-500">{(ev.relevance * 100).toFixed(0)}%</span>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ContractDetail;
