import React from "react";
import { XMarkIcon } from "@heroicons/react/24/outline";

const EvidenceDrawer = ({ open, close, evidence }) => {
  return (
    <div
      className={`fixed top-0 right-0 h-full w-96 bg-white shadow-lg transform transition-transform z-50 ${
        open ? "translate-x-0" : "translate-x-full"
      }`}
    >
      <div className="flex justify-between items-center p-4 border-b">
        <h2 className="text-xl font-bold text-indigo-600">Evidence Panel</h2>
        <button onClick={close}>
          <XMarkIcon className="w-6 h-6 text-gray-600" />
        </button>
      </div>
      <div className="p-4 overflow-y-auto h-full space-y-4">
        {evidence.length === 0 ? (
          <p className="text-gray-500">No evidence available.</p>
        ) : (
          evidence.map((e, idx) => (
            <div key={idx} className="border rounded p-3 bg-gray-50 shadow-sm">
              <p className="text-sm text-gray-600">Source: {e.source}</p>
              <p className="text-gray-700 mt-1">{e.snippet}</p>
              <p className="text-sm text-gray-500 mt-1">Relevance: {Math.round(e.relevance * 100)}%</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default EvidenceDrawer;
