import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";

const ContractDetail = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);

  useEffect(() => {
    fetch(`/contracts.json`)
      .then((res) => res.json())
      .then((data) => {
        const found = data.find((c) => c.id === id);
        setContract(found);
      });
  }, [id]);

  if (!contract) return <p className="p-6">Loading contract...</p>;

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1">
        <Topbar />
        <div className="p-6">
          <h2 className="text-2xl font-bold mb-4">{contract.name}</h2>
          <p><strong>Parties:</strong> {contract.parties}</p>
          <p><strong>Expiry:</strong> {contract.expiry}</p>
          <p><strong>Status:</strong> {contract.status}</p>
          <p><strong>Risk:</strong> {contract.risk}</p>
        </div>
      </div>
    </div>
  );
};

export default ContractDetail;
