import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FolderIcon, LightBulbIcon, ChartBarIcon, Cog6ToothIcon } from "@heroicons/react/24/outline";

const Sidebar = () => {
  const location = useLocation();

  const links = [
    { name: "Contracts", path: "/dashboard", icon: FolderIcon },
    { name: "Insights", path: "#", icon: LightBulbIcon, disabled: true },
    { name: "Reports", path: "#", icon: ChartBarIcon, disabled: true },
    { name: "Settings", path: "#", icon: Cog6ToothIcon, disabled: true },
  ];

  return (
    <div className="w-64 bg-gray-50 shadow-lg h-screen p-6 flex flex-col">
      <h2 className="text-xl font-bold mb-8 text-gray-800">Contracts Dashboard</h2>
      <nav className="flex flex-col gap-3">
        {links.map((link) => {
          const isActive = location.pathname === link.path;
          return link.disabled ? (
            <div
              key={link.name}
              className="flex items-center gap-2 text-gray-400 cursor-not-allowed px-3 py-2 rounded hover:bg-gray-100"
            >
              <link.icon className="w-5 h-5" />
              {link.name}
            </div>
          ) : (
            <Link
              key={link.name}
              to={link.path}
              className={`flex items-center gap-2 px-3 py-2 rounded transition ${
                isActive
                  ? "bg-blue-600 text-white"
                  : "text-gray-700 hover:bg-blue-100 hover:text-blue-700"
              }`}
            >
              <link.icon className="w-5 h-5" />
              {link.name}
            </Link>
          );
        })}
      </nav>
    </div>
  );
};

export default Sidebar;
