import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";

const ContractDetail = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    fetch("/contracts.json")
      .then((res) => res.json())
      .then((data) => {
        const found = data.find((c) => c.id === id);
        setContract(found);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [id]);

  if (loading) return <p className="p-6">Loading contract...</p>;
  if (!contract) return <p className="p-6 text-red-500">Contract not found.</p>;

  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1">
        <Topbar />
        <div className="p-6 space-y-6">
          {/* Metadata */}
          <div className="bg-white shadow rounded-lg p-6">
            <h2 className="text-3xl font-bold mb-4 text-blue-700">{contract.name}</h2>
            <div className="grid grid-cols-2 gap-4">
              <p><strong>Parties:</strong> {contract.parties}</p>
              <p><strong>Start:</strong> {contract.start}</p>
              <p><strong>Expiry:</strong> {contract.expiry}</p>
              <p><strong>Status:</strong> <span className={`font-semibold ${contract.status === "Active" ? "text-green-600" : contract.status === "Renewal Due" ? "text-yellow-600" : "text-red-600"}`}>{contract.status}</span></p>
              <p><strong>Risk:</strong> <span className={`font-semibold ${contract.risk === "High" ? "text-red-600" : contract.risk === "Medium" ? "text-yellow-600" : "text-green-600"}`}>{contract.risk}</span></p>
            </div>
          </div>

          {/* Clauses */}
          <div className="bg-white shadow rounded-lg p-6">
            <h3 className="text-xl font-semibold mb-4">Clauses</h3>
            <div className="grid md:grid-cols-2 gap-4">
              {contract.clauses.map((clause, idx) => (
                <div key={idx} className="border rounded p-4 hover:shadow-lg transition">
                  <h4 className="font-semibold">{clause.title}</h4>
                  <p className="text-gray-600">{clause.summary}</p>
                  <p className="mt-2 text-sm text-gray-500">Confidence: {(clause.confidence * 100).toFixed(0)}%</p>
                </div>
              ))}
            </div>
          </div>

          {/* AI Insights */}
          <div className="bg-white shadow rounded-lg p-6">
            <h3 className="text-xl font-semibold mb-4">AI Insights</h3>
            <ul className="space-y-2">
              {contract.insights.map((insight, idx) => (
                <li key={idx} className={`p-3 rounded ${insight.risk === "High" ? "bg-red-100 text-red-700" : insight.risk === "Medium" ? "bg-yellow-100 text-yellow-700" : "bg-green-100 text-green-700"}`}>
                  {insight.message} <span className="font-semibold">({insight.risk} Risk)</span>
                </li>
              ))}
            </ul>
          </div>

          {/* Evidence Panel */}
          <div className="bg-white shadow rounded-lg p-6 relative">
            <button
              className="absolute top-4 right-4 bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              onClick={() => setDrawerOpen(!drawerOpen)}
            >
              {drawerOpen ? "Close Evidence" : "View Evidence"}
            </button>
            {drawerOpen && (
              <div className="mt-6 border-t pt-4 space-y-4">
                <h4 className="font-semibold text-lg">Evidence</h4>
                {contract.evidence.map((e, idx) => (
                  <div key={idx} className="p-3 border rounded hover:shadow-sm transition">
                    <p className="text-gray-600">{e.snippet}</p>
                    <p className="text-sm text-gray-500">Source: {e.source}</p>
                    <p className="text-sm text-gray-500">Relevance: {(e.relevance * 100).toFixed(0)}%</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContractDetail;
