import React, { useState } from "react";
import {
  XCircleIcon,
  CheckCircleIcon,
  CloudArrowUpIcon,
  ArrowLeftIcon,
} from "@heroicons/react/24/outline";
import { motion, AnimatePresence } from "framer-motion";

const UploadModal = ({ closeModal }) => {
  const [files, setFiles] = useState([]);

  const handleFiles = (selectedFiles) => {
    const filesArray = Array.from(selectedFiles).map((file) => ({
      file,
      status: "Uploading",
    }));
    setFiles((prev) => [...prev, ...filesArray]);

    filesArray.forEach((f, idx) => {
      setTimeout(() => {
        setFiles((prev) =>
          prev.map((item) =>
            item === f
              ? { ...item, status: Math.random() > 0.2 ? "Success" : "Error" }
              : item
          )
        );
      }, 1500 + idx * 500);
    });
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFiles(e.dataTransfer.files);
  };

  const handleBrowse = (e) => handleFiles(e.target.files);

  const removeFile = (index) => setFiles((prev) => prev.filter((_, idx) => idx !== index));

  return (
    <AnimatePresence>
      <motion.div
        className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50"
        onClick={closeModal}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <motion.div
          className="bg-white w-96 rounded-xl shadow-2xl p-6 relative"
          onClick={(e) => e.stopPropagation()}
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.9, opacity: 0 }}
        >
          {/* Back Button */}
          <button
            onClick={closeModal}
            className="absolute top-3 left-3 text-gray-500 hover:text-gray-700 flex items-center gap-1"
          >
            <ArrowLeftIcon className="w-5 h-5" /> Back
          </button>

          <h2 className="text-xl font-bold mb-4 text-blue-700 flex items-center justify-center gap-2">
            <CloudArrowUpIcon className="w-6 h-6" /> Upload Files
          </h2>

          <div
            className="border-2 border-dashed border-gray-300 rounded-lg h-32 flex flex-col items-center justify-center mb-4 cursor-pointer hover:border-blue-500 transition relative"
            onDrop={handleDrop}
            onDragOver={(e) => e.preventDefault()}
          >
            <p className="text-gray-500">Drag & drop files here or click to browse</p>
            <input
              type="file"
              multiple
              className="absolute w-full h-full opacity-0 cursor-pointer"
              onChange={handleBrowse}
            />
          </div>

          <div className="max-h-60 overflow-y-auto space-y-2">
            {files.length === 0 && (
              <p className="text-gray-400 text-sm text-center">No files uploaded yet.</p>
            )}
            {files.map((f, idx) => (
              <motion.div
                key={idx}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                className="flex items-center justify-between p-2 border rounded hover:shadow-md transition"
              >
                <div>
                  <p className="font-semibold">{f.file.name}</p>
                  <p
                    className={`text-sm ${
                      f.status === "Uploading"
                        ? "text-gray-500"
                        : f.status === "Success"
                        ? "text-green-600"
                        : "text-red-600"
                    }`}
                  >
                    {f.status}
                  </p>
                </div>
                <div className="flex items-center gap-2">
                  {f.status === "Success" && (
                    <CheckCircleIcon className="w-5 h-5 text-green-600" />
                  )}
                  {f.status === "Error" && <XCircleIcon className="w-5 h-5 text-red-600" />}
                  <button
                    onClick={() => removeFile(idx)}
                    className="text-gray-400 hover:text-gray-600"
                  >
                    ✕
                  </button>
                </div>
              </motion.div>
            ))}
          </div>

          <button
            className="mt-4 w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            onClick={() => setFiles([])}
          >
            Clear All
          </button>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default UploadModal;
