import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";

const ContractDetail = () => {
  const { id } = useParams();
  const [contract, setContract] = useState(null);
  const [showEvidence, setShowEvidence] = useState(false);

  useEffect(() => {
    fetch(`/contracts.json`)
      .then((res) => res.json())
      .then((data) => {
        const found = data.find((c) => c.id === id);
        setContract(found);
      });
  }, [id]);

  if (!contract) return <p className="p-6 text-gray-500">Loading contract...</p>;

  return (
    <div className="flex h-screen bg-gray-100">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Topbar />
        <div className="p-6 overflow-auto">
          <h1 className="text-3xl font-bold mb-6 text-gray-800">{contract.name}</h1>

          {/* Metadata Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <div className="bg-white p-4 rounded shadow hover:shadow-lg transition">
              <p className="text-gray-500">Parties</p>
              <p className="font-semibold">{contract.parties}</p>
            </div>
            <div className="bg-white p-4 rounded shadow hover:shadow-lg transition">
              <p className="text-gray-500">Start Date</p>
              <p className="font-semibold">{contract.start}</p>
            </div>
            <div className="bg-white p-4 rounded shadow hover:shadow-lg transition">
              <p className="text-gray-500">Expiry Date</p>
              <p className="font-semibold">{contract.expiry}</p>
            </div>
            <div className="bg-white p-4 rounded shadow hover:shadow-lg transition">
              <p className="text-gray-500">Status / Risk</p>
              <div className="flex gap-2 mt-1">
                <span
                  className={`px-2 py-1 rounded text-white font-semibold ${
                    contract.status === "Active"
                      ? "bg-green-500"
                      : contract.status === "Renewal Due"
                      ? "bg-yellow-500"
                      : "bg-gray-500"
                  }`}
                >
                  {contract.status}
                </span>
                <span
                  className={`px-2 py-1 rounded text-white font-semibold ${
                    contract.risk === "High"
                      ? "bg-red-600"
                      : contract.risk === "Medium"
                      ? "bg-yellow-600"
                      : "bg-green-600"
                  }`}
                >
                  {contract.risk}
                </span>
              </div>
            </div>
          </div>

          {/* Clauses */}
          <h2 className="text-2xl font-semibold mb-4">Clauses</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
            {contract.clauses.map((clause, idx) => (
              <div
                key={idx}
                className="bg-white p-4 rounded shadow hover:shadow-lg transition"
              >
                <p className="font-bold">{clause.title}</p>
                <p className="text-gray-600">{clause.summary}</p>
                <p className="mt-2 text-sm text-gray-500">
                  Confidence: {(clause.confidence * 100).toFixed(0)}%
                </p>
              </div>
            ))}
          </div>

          {/* AI Insights */}
          <h2 className="text-2xl font-semibold mb-4">AI Insights</h2>
          <div className="space-y-3 mb-6">
            {contract.insights.map((insight, idx) => (
              <div
                key={idx}
                className={`p-4 rounded shadow ${
                  insight.risk === "High"
                    ? "bg-red-100 border-l-4 border-red-600"
                    : insight.risk === "Medium"
                    ? "bg-yellow-100 border-l-4 border-yellow-600"
                    : "bg-green-100 border-l-4 border-green-600"
                }`}
              >
                <p className="font-semibold">{insight.risk} Risk</p>
                <p className="text-gray-700">{insight.message}</p>
              </div>
            ))}
          </div>

          {/* Evidence Panel Toggle */}
          <button
            onClick={() => setShowEvidence(!showEvidence)}
            className="mb-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
          >
            {showEvidence ? "Hide Evidence" : "Show Evidence"}
          </button>

          {/* Evidence Panel */}
          {showEvidence && (
            <div className="bg-white p-4 rounded shadow space-y-3">
              {contract.evidence.map((ev, idx) => (
                <div key={idx} className="border-b pb-2">
                  <p className="font-semibold">{ev.source}</p>
                  <p className="text-gray-700">{ev.snippet}</p>
                  <p className="text-sm text-gray-500">
                    Relevance: {(ev.relevance * 100).toFixed(0)}%
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ContractDetail;
