import React, { useState } from "react";
import { XCircleIcon, CheckCircleIcon, CloudArrowUpIcon } from "@heroicons/react/24/outline";

const UploadModal = ({ closeModal }) => {
  const [files, setFiles] = useState([]);

  const handleFiles = (selectedFiles) => {
    const filesArray = Array.from(selectedFiles).map((file) => ({
      file,
      status: "Uploading",
    }));
    setFiles((prev) => [...prev, ...filesArray]);

    filesArray.forEach((f, idx) => {
      setTimeout(() => {
        setFiles((prev) =>
          prev.map((item) =>
            item === f
              ? { ...item, status: Math.random() > 0.2 ? "Success" : "Error" }
              : item
          )
        );
      }, 1500 + idx * 500);
    });
  };

  const handleDrop = (e) => {
    e.preventDefault();
    handleFiles(e.dataTransfer.files);
  };

  const handleBrowse = (e) => handleFiles(e.target.files);

  const removeFile = (index) => setFiles((prev) => prev.filter((_, idx) => idx !== index));

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50"
      onClick={closeModal} // close on outside click
    >
      <div
        className="bg-white w-96 rounded-lg shadow-lg p-6"
        onClick={(e) => e.stopPropagation()} // prevent modal click from closing
      >
        <h2 className="text-xl font-bold mb-4 text-blue-700 flex items-center gap-2">
          <CloudArrowUpIcon className="w-6 h-6" /> Upload Files
        </h2>

        <div
          className="border-2 border-dashed border-gray-300 rounded h-32 flex flex-col items-center justify-center mb-4 cursor-pointer hover:border-blue-500 transition"
          onDrop={handleDrop}
          onDragOver={(e) => e.preventDefault()}
        >
          <p className="text-gray-500">Drag & drop files here or click to browse</p>
          <input
            type="file"
            multiple
            className="absolute w-full h-full opacity-0 cursor-pointer"
            onChange={handleBrowse}
          />
        </div>

        <div className="max-h-60 overflow-y-auto space-y-2">
          {files.map((f, idx) => (
            <div
              key={idx}
              className="flex items-center justify-between p-2 border rounded hover:shadow-sm transition"
            >
              <div>
                <p className="font-semibold">{f.file.name}</p>
                <p
                  className={`text-sm ${
                    f.status === "Uploading"
                      ? "text-gray-500"
                      : f.status === "Success"
                      ? "text-green-600"
                      : "text-red-600"
                  }`}
                >
                  {f.status}
                </p>
              </div>
              <div className="flex items-center gap-2">
                {f.status === "Success" && <CheckCircleIcon className="w-5 h-5 text-green-600" />}
                {f.status === "Error" && <XCircleIcon className="w-5 h-5 text-red-600" />}
                <button
                  onClick={() => removeFile(idx)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  âœ•
                </button>
              </div>
            </div>
          ))}
          {files.length === 0 && <p className="text-gray-400 text-sm">No files uploaded yet.</p>}
        </div>

        <button
          className="mt-4 w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          onClick={() => setFiles([])}
        >
          Clear All
        </button>
      </div>
    </div>
  );
};

export default UploadModal;
