import React, { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { ChevronDownIcon } from "@heroicons/react/24/outline";

const Topbar = () => {
  const { logout } = useContext(AuthContext);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  return (
    <div className="flex justify-between items-center bg-white p-4 shadow-md">
      {/* Page Title */}
      <h1 className="text-xl font-semibold text-gray-800">Contracts</h1>

      {/* Profile / Logout Dropdown */}
      <div className="relative">
        <button
          onClick={() => setDropdownOpen(!dropdownOpen)}
          className="flex items-center gap-2 bg-gray-100 px-3 py-1 rounded-full hover:bg-gray-200 transition"
        >
          <div className="w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center font-bold">
            U
          </div>
          <ChevronDownIcon className="w-4 h-4 text-gray-600" />
        </button>

        {dropdownOpen && (
          <div className="absolute right-0 mt-2 w-40 bg-white shadow-lg rounded-lg overflow-hidden z-10">
            <button
              onClick={logout}
              className="w-full text-left px-4 py-2 hover:bg-red-500 hover:text-white transition"
            >
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Topbar;
