import React from "react";
import { Link } from "react-router-dom";

const statusColors = {
  Active: "bg-green-100 text-green-800",
  "Renewal Due": "bg-yellow-100 text-yellow-800",
  Expired: "bg-red-100 text-red-800",
};

const riskColors = {
  Low: "bg-green-200 text-green-900",
  Medium: "bg-yellow-200 text-yellow-900",
  High: "bg-red-200 text-red-900",
};

const Table = ({ contracts }) => {
  return (
    <div className="overflow-x-auto shadow-lg rounded-lg">
      <table className="w-full border-collapse min-w-[600px]">
        <thead>
          <tr className="bg-gray-100 text-left">
            <th className="p-3 rounded-tl-lg">Contract Name</th>
            <th className="p-3">Parties</th>
            <th className="p-3">Expiry Date</th>
            <th className="p-3">Status</th>
            <th className="p-3 rounded-tr-lg">Risk Score</th>
          </tr>
        </thead>
        <tbody>
          {contracts.map((c, index) => (
            <tr
              key={c.id}
              className={`border-b transition-colors hover:bg-gray-50 ${
                index % 2 === 0 ? "bg-white" : "bg-gray-50"
              }`}
            >
              <td className="p-3 text-blue-600 font-medium hover:underline">
                <Link to={`/contracts/${c.id}`}>{c.name}</Link>
              </td>
              <td className="p-3">{c.parties}</td>
              <td className="p-3">{c.expiry}</td>
              <td className="p-3">
                <span
                  className={`px-2 py-1 rounded-full text-sm font-semibold ${statusColors[c.status]}`}
                >
                  {c.status}
                </span>
              </td>
              <td className="p-3">
                <span
                  className={`px-2 py-1 rounded-full text-sm font-semibold ${riskColors[c.risk]}`}
                >
                  {c.risk}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
