import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";
import Table from "../components/Table";
import UploadModal from "../components/UploadModal";
import { motion, AnimatePresence } from "framer-motion";

const Dashboard = () => {
  const [contracts, setContracts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("All");
  const [riskFilter, setRiskFilter] = useState("All");
  const [showUpload, setShowUpload] = useState(false);

  useEffect(() => {
    fetch("/contracts.json")
      .then((res) => res.json())
      .then((data) => {
        setContracts(data);
        setLoading(false);
      })
      .catch(() => {
        setError(true);
        setLoading(false);
      });
  }, []);

  const filteredContracts = contracts.filter((c) => {
    return (
      (c.name.toLowerCase().includes(search.toLowerCase()) ||
        c.parties.toLowerCase().includes(search.toLowerCase())) &&
      (statusFilter === "All" || c.status === statusFilter) &&
      (riskFilter === "All" || c.risk === riskFilter)
    );
  });

  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar />

      <div className="flex-1 flex flex-col">
        <Topbar openUpload={() => setShowUpload(true)} />

        <div className="p-6 overflow-auto flex-1">
          <h2 className="text-3xl font-bold mb-6 text-indigo-600">Contracts Dashboard</h2>

          {/* Search & Filters */}
          <div className="flex flex-col md:flex-row gap-4 mb-6">
            <input
              type="text"
              placeholder="Search by name or parties"
              className="p-3 border rounded-lg shadow-sm w-full md:w-1/3 focus:ring-2 focus:ring-indigo-400 focus:outline-none transition"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <select
              className="p-3 border rounded-lg shadow-sm w-full md:w-1/4 focus:ring-2 focus:ring-indigo-400 focus:outline-none transition"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option>All</option>
              <option>Active</option>
              <option>Expired</option>
              <option>Renewal Due</option>
            </select>
            <select
              className="p-3 border rounded-lg shadow-sm w-full md:w-1/4 focus:ring-2 focus:ring-indigo-400 focus:outline-none transition"
              value={riskFilter}
              onChange={(e) => setRiskFilter(e.target.value)}
            >
              <option>All</option>
              <option>Low</option>
              <option>Medium</option>
              <option>High</option>
            </select>
          </div>

          {/* Content States */}
          <AnimatePresence>
            {loading && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="text-indigo-500 text-lg"
              >
                Loading contracts...
              </motion.div>
            )}
            {error && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="text-red-500 text-lg"
              >
                Failed to load contracts. Please try again later.
              </motion.div>
            )}
            {!loading && filteredContracts.length === 0 && !error && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="text-gray-500 text-center py-20"
              >
                <p className="text-2xl font-medium">No contracts found.</p>
                <p className="mt-2">Try adjusting your filters or uploading new contracts.</p>
              </motion.div>
            )}
            {!loading && filteredContracts.length > 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                <Table contracts={filteredContracts} />
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>

      {/* Upload Modal */}
      {showUpload && <UploadModal closeModal={() => setShowUpload(false)} />}
    </div>
  );
};

export default Dashboard;
