import React from "react";

const ClauseCard = ({ title, summary, confidence }) => {
  const confidencePercent = Math.round(confidence * 100);
  return (
    <div className="border rounded-lg p-4 shadow hover:shadow-md transition bg-white">
      <h3 className="font-semibold text-lg text-indigo-600">{title}</h3>
      <p className="text-gray-700 mt-1">{summary}</p>
      <p className="text-sm text-gray-500 mt-2">Confidence: {confidencePercent}%</p>
    </div>
  );
};

export default ClauseCard;
